import { Component, OnInit } from '@angular/core';
import { UserService, MovieEntity } from './user.service';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['../shared/shared.component.css']
})
export class UserComponent implements OnInit {
  searchQuery = '';
  searchResults: MovieEntity[] = [];
  localMovies: MovieEntity[] = [];

  constructor(private userService: UserService) {}

  ngOnInit(): void {
    this.loadLocalMovies();
  }

  onSearch() {
    this.userService.searchMovies(this.searchQuery).subscribe({
      next: (results) => {
        this.searchResults = results;
      },
      error: (err) => {
        console.error(err);
      }
    });
  }

  loadLocalMovies() {
    this.userService.getAllMovies().subscribe({
      next: (movies) => {
        this.localMovies = movies;
      },
      error: (err) => {
        console.error(err);
      }
    });
  }
}
<div class="center-container">
  <h2>User Dashboard</h2>

  <div>
    <input [(ngModel)]="searchQuery" placeholder="Search movies..." />
    <button (click)="onSearch()">Search</button>
  </div>


  <div *ngIf="searchResults.length > 0">
    <h3>Search Results</h3>
    <ul class="movie-list">
      <li *ngFor="let movie of searchResults" class="movie-item">
        <img [src]="movie.posterUrl" alt="{{ movie.title }}" class="movie-poster" />
        <span class="movie-info">
          {{ movie.title }} ({{ movie.year }})
        </span>
      </li>
    </ul>
  </div>


  <div>
    <h3>Local Movies in DB</h3>
    <ul class="movie-list">
      <li *ngFor="let movie of localMovies" class="movie-item">
        <img [src]="movie.posterUrl" alt="{{ movie.title }}" class="movie-poster" />
        <span class="movie-info">
          {{ movie.title }} ({{ movie.year }})
        </span>
      </li>
    </ul>
  </div>
</div>
