// src/app/admin/admin.component.ts
import { Component, OnInit } from '@angular/core';
import { AdminService, MovieDTO, MovieEntity } from './admin.service';

@Component({
  selector: 'app-admin',
  templateUrl: './admin.component.html'
})
export class AdminComponent implements OnInit {
  searchQuery = '';
  searchResults: MovieDTO[] = [];
  localMovies: MovieEntity[] = [];

  constructor(private adminService: AdminService) {}

  ngOnInit(): void {
    // Load the existing local DB movies
    this.loadLocalMovies();
  }

  onSearch() {
    this.adminService.searchMovies(this.searchQuery).subscribe({
      next: (results) => {
        this.searchResults = results;
      },
      error: (err) => {
        console.error(err);
      }
    });
  }

  onAdd(movie: MovieDTO) {
    this.adminService.addMovie(movie).subscribe({
      next: (savedMovie) => {
        // Refresh local movies list
        this.loadLocalMovies();
      },
      error: (err) => {
        console.error(err);
      }
    });
  }

  onDelete(movieId: number) {
    this.adminService.deleteMovie(movieId).subscribe({
      next: (msg) => {
        console.log(msg);
        this.loadLocalMovies();
      },
      error: (err) => {
        console.error(err);
      }
    });
  }

  loadLocalMovies() {
    this.adminService.listAllMovies().subscribe({
      next: (movies) => {
        this.localMovies = movies;
      },
      error: (err) => {
        console.error(err);
      }
    });
  }
}
<!-- src/app/admin/admin.component.html -->
<h2>Admin Dashboard</h2>

<!-- Search Section -->
<div>
  <input [(ngModel)]="searchQuery" placeholder="Search movies..." />
  <button (click)="onSearch()">Search</button>
</div>

<!-- Search Results -->
<div *ngIf="searchResults.length > 0">
  <h3>Search Results</h3>
  <ul>
    <li *ngFor="let movie of searchResults">
      {{ movie.Title }} ({{ movie.Year }})
      <button (click)="onAdd(movie)">Add to DB</button>
    </li>
  </ul>
</div>

<!-- Local DB Movies -->
<div>
  <h3>Local Movies in DB</h3>
  <ul>
    <li *ngFor="let movie of localMovies">
      {{ movie.title }} ({{ movie.year }})
      <button (click)="onDelete(movie.id)">Delete</button>
    </li>
  </ul>
</div>
